sys_script_client.script:
function onLoad() {
  // Hide all sensitive fields by default (failsafe)
  var internalFields = [
    'requires_approval',
    'approval', 
    'test_type',
    'state',
    'source',
    'customer_type',
    'assigned_to',
    'assignment_group',
    'quantity',
    'delivery_address',
    'request_type',
	'approval_state'
  ];

  // Hide all fields initially
  internalFields.forEach(function(field) {
    g_form.setDisplay(field, false);
  });

  // Show basic customer fields
  if (g_user.hasRole('x_1662488_pcpm.customer')) {
    g_form.setDisplay('short_description', true);
    g_form.setDisplay('description', true);
    g_form.setDisplay('ticket_type', true);
    g_form.setDisplay('category', true);
    g_form.setDisplay('related_product', true);
    g_form.setDisplay('quantity', true);
    g_form.setDisplay('delivery_address', true);
    // These fields should be defaulted or filled automatically: source, customer_type
  }

  // Support agents see everything
  if (g_user.hasRole('x_1662488_pcpm.support') || g_user.hasRole('x_1662488_pcpm.admin')) {
    internalFields.forEach(function(field) {
      g_form.setDisplay(field, true);
    });
    g_form.setDisplay('short_description', true);
    g_form.setDisplay('description', true);
    g_form.setDisplay('ticket_type', true);
    g_form.setDisplay('category', true);
    g_form.setDisplay('related_product', true);
    g_form.setDisplay('quantity', true);
    g_form.setDisplay('delivery_address', true);
  }

  // Testers see relevant internal testing fields
  if (g_user.hasRole('x_1662488_pcpm.tester')) {
    g_form.setDisplay('short_description', true);
    g_form.setDisplay('description', true);
    g_form.setDisplay('ticket_type', true);
    g_form.setDisplay('category', true);
    g_form.setDisplay('related_product', true);
    g_form.setDisplay('test_type', true);
    g_form.setDisplay('requires_approval', true);
  }
}
